import React from 'react'
const Accordiom = (props) => {
  const deleteitem=(id)=>{
    props.deleteItem(id)
  }
  return (
    <>
    {props.data.length?(
      props.data.map((item,index)=>{
        return  <div className='mt-4' key={props.data.id}>
        <details>
          <summary>
              {item.questions}
          </summary>
          <div>
              <p>{item.answers}</p>
              <button className='buttn buttn2 p-1' onClick={()=>{deleteitem(item.id)}}>مسح </button>
          </div>
        </details>
      </div>
      })
    ):(<h2 className='mt-3'>لا يوجد اسئلة الان</h2>)}{

    props.data.length?(
      <button className='buttn buttna mt-2' onClick={props.delete}>مسح الكل</button>

    ):(null)}
    </>
  )
}

export default Accordiom
